import { Notification } from "../NotificationManager";
/**
 * Notification options for the browser notification handler
 *
 * @typedef {object} BrowserNotificationOptions
 * @property {string} [title] Notification heading
 * @property {string} [body] Notification content
 * @property {Function} [onClick] onclick event handler function
 * @property {Function} [onClose] onClose event handler function
 * @property {Function} [onShow] onShow event handler function
 * @property {Function} [onError] onError event handler function
 * @property {object} [options] The Notification options object from the JS Notifications API
 * @memberof NotificationManager
 */
export interface BrowserNotificationOptions {
    title?: string;
    body?: string;
    onClick?: (notification: Notification, event: Event) => void;
    onClose?: (notification: Notification, event: Event) => void;
    onShow?: (notification: Notification, event: Event) => void;
    onError?: (notification: Notification, event: Event) => void;
    options?: NotificationOptions;
}
