import { ChannelsState } from "./ChannelsState";
import { ChatChannelInputState } from "./ChatChannelInputState";
import { ChatMessageListState } from "./ChatMessageListState";
import { ChatSessionState } from "./ChatSessionState";
import { ChatUserState } from "./ChatUserState";
import { ConversationInputState } from "./ConversationInputState";
import { ConversationsState } from "./ConversationsState";
/**
 * Chat state
 *
 * @category State
 * @typedef {object} ChatState
 * @property {ConversationsState} conversations State of conversations
 * @property {ChatSessionState} session State of the user session
 * @property {ChatUserState} users State of chat users
 * @property {ConversationInputState} conversationInput Conversation text input state
 * @property {ChatMessageListState} messageList State of message list
 * @memberof AppState
 */
export interface ChatState {
    readonly channels: ChannelsState;
    readonly conversations: ConversationsState;
    readonly session: ChatSessionState;
    readonly users: ChatUserState;
    readonly channelInput: ChatChannelInputState;
    readonly conversationInput: ConversationInputState;
    readonly messageList: ChatMessageListState;
}
