import { AvailableLocale, LocaleSource } from "../locale.definitions";
/**
 * Localization Type object containing the locale tag, available locales, and a function to set the locale preference
 * @interface LocalizationType
 * @property {string} localeTag The preferred locale of the user
 * @property {Array<AvailableLocale>} availableLocales The available locales in Flex UI
 * @property {Function} setLocalePreference An async function which sets the preference of the user
 * @category Localization
 */
export interface LocalizationType {
    localeTag: string;
    availableLocales: Array<AvailableLocale>;
    setLocalePreference: (locale: string) => Promise<void>;
    localeSource: LocaleSource;
}
